tokenContract = web3.eth.contract(contract_id, abi=tokenAbi)
tokensymbol = tokenContract.functions.symbol().call()


phase = int(input("Enter phase no dec(not hexadecimal)"))

sender_address = config.vholder_address #TokenAddress of holder

epoch = float(contract.functions.unlockTime(contract_id, phase).call())

print(f"it willl unlock at {epoch}")

token_value = contract.functions.getUserPhaseTokenClaim(contract_id, phase, sender_address).call()

print(f"You can claim {token_value} {tokensymbol}")

userchoice = input("Do you want to claim all tokens???")

if(userchoice == '0'):
    sys.exit()

else:
    seconds = time.time()

    while (seconds < (epoch + 1)):
        print("Current time in seconds since epoch =", seconds)
        seconds = time.time()
        time.sleep(1)
    
    txn = contract.functions.claim(
        contract_id,
        phase
         ).buildTransaction({
           'from': sender_address,
           'chainId': 43114,
           'gas': 200000,
           'maxFeePerGas': web3.toWei('300','gwei'),
            'maxPriorityFeePerGas': web3.toWei('1','gwei'),
           'nonce': web3.eth.get_transaction_count(sender_address),
                })
                        
    signed_txn = web3.eth.account.sign_transaction(txn, private_key=config.private)
    tx_token = web3.eth.send_raw_transaction(signed_txn.rawTransaction)
    print(f"Claimed {tokensymbol}: " + web3.toHex(tx_token))


